import React, { useState } from 'react';
import './App.css';

function App() {
  const [location, setLocation] = useState('start');
  type GameText = {
    [key: string]: {
      description: string;
      options: { text: string; nextLocation: string; }[];
    };
  };
  const gameText = {
    start: {
      description: 'You are in a dark room. There is a door to the north.',
      options: [
        { text: 'Go north', nextLocation: 'hallway' },
      ],
    },
    hallway: {
      description: 'You are in a hallway. There is a door to the south and a door to the east.',
      options: [
        { text: 'Go south', nextLocation: 'start' },
        { text: 'Go east', nextLocation: 'treasureRoom' },
      ],
    },
    treasureRoom: {
      description: 'You found a room filled with treasure! You win!',
      options: [
        { text: 'Play again', nextLocation: 'start' },
      ],
    },
  };

  const handleOptionClick = (nextLocation: string) => {
    setLocation(nextLocation);
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>{gameText[location].description}</p>
        <div>
          {gameText[location].options.map((option, index) => (
            <button key={index} onClick={() => handleOptionClick(option.nextLocation)}>
              {option.text}
            </button>
          ))}
        </div>
      </header>
    </div>
  );
}

export default App;
